import React, { Component } from 'react';

const Qno = 6;
function Rectangle(props){
  if(props.green)
    return (
      <div className="Rectangle bg-success">
        <p className="text-light"><b>{props.foodName}</b></p>
      </div>
    );
  else 
    return (
      <div className="Rectangle bg-danger">
        <p className="text-light"><b>{props.foodName}</b></p>
      </div>
  );
}

function StartScreen(props){
  return (
    <div>
      <p style="font-size:36px">Compare your tastes with {props.author}</p>
      <p>Plz plz input ur name!:</p>
      <br />
      <input type="text" onChange={props.onChange} value={props.name} />
      <button onClick={props.onClick}>Start!</button>
    </div>
  );
}

function QuestionScreen(props){
  return(
    <div>
      <p>QUESTION {props.number}/{Qno}: {props.question}</p>
      <button data-choicename="like" onClick={props.onClick}>LIKE!</button>
      <button data-choicename="dislike" onClick={props.onClick}>DISLIKE!</button>
    </div>
  );
}

function ResultScreen(props){
  return(
    <div>
      <p>your tastes match about {props.score}% with {props.author}!</p>
      <Rectangle foodName={props.questions[0].subject} green={props.answers[0]=="like"}/>
      <Rectangle foodName={props.questions[1].subject} green={props.answers[1]=="like"}/>
      <Rectangle foodName={props.questions[2].subject} green={props.answers[2]=="like"}/>
      <Rectangle foodName={props.questions[3].subject} green={props.answers[3]=="like"}/>
      <Rectangle foodName={props.questions[4].subject} green={props.answers[4]=="like"}/>
      <Rectangle foodName={props.questions[5].subject} green={props.answers[5]=="like"}/>
      <button onClick={props.onClick}>Try again?</button>
    </div>
  );
}

class HobulhoApp extends Component {
  constructor(props){
    super(props);
    this.props = props;
    this.state = {
      step:0,
      name:"anon",
      answers:{
        1:"dislike",
        2:"dislike",
        3:"dislike",
        4:"dislike",
        5:"dislike",
        6:"dislike",
      },
    }

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleNameSubmit = this.handleNameSubmit.bind(this);
    this.handleChoiceClick = this.handleChoiceClick.bind(this);
    this.handleBackToHomeClick = this.handleBackToHomeClick.bind(this);
  }
  handleNameChange(event){
    this.setState({
      name: event.target.value,
    });
  }
  handleNameSubmit(event){
    this.setState({
      step: 1,
    })
  }
  handleChoiceClick(event){
    const n = this.state.answers;
    n[this.state.step] = event.target.getAttribute('data-choicename');
    this.setState({
      step: this.state.step + 1,
      answers: n,
    })
  }
  handleBackToHomeClick(event){
    this.setState({
      step:0,
    });
  }
  evaluateScore(){
    var counter = 0;
    const listOne = Object.values(this.state.answers);
    const listTwo = this.props.questions.map((val,ind)=>val.choice);
    for(var i=0;i<listOne.length;i++){
      if(listOne[i]===listTwo[i]) counter++;
    }
    if(listOne.length) return Math.round(100 * counter / listOne.length);
    else return null;
  }
  render() {
    const st = this.state.step;
    if(st === 0){
      return (
        <StartScreen onChange={this.handleNameChange} onClick={this.handleNameSubmit} name={this.state.name} author={this.props.author}/>  
      );
    }else if(st && st <= Qno){
      return <QuestionScreen onClick={this.handleChoiceClick} question={this.props.questions[st-1].subject} number={st}/>
    }else{
      const s = this.evaluateScore();
      return <ResultScreen onClick={this.handleBackToHomeClick} score={s} questions={this.props.questions} answers={this.state.answers} author={this.props.author}/>
    }

  }
}

export default HobulhoApp;
