import React, { Component } from 'react';

function StartScreen(props){
  return (
    <div>
      <input type="text" onChange={props.onChange} value={props.name} />
      <button onClick={props.onClick}>Start!</button>
    </div>
  );
}

class HobulhoApp extends Component {
  constructor(props){
    super(props);
    this.props = props;
    this.state = {
      step:0,
      name:"anon",
      answers:{
        1:"dislike",
        2:"dislike",
        3:"dislike",
        4:"dislike",
        5:"dislike",
        6:"dislike",
      },
    }

    this.handleNameChange.bind(this);
    this.handleNameSubmit.bind(this);
  }
  handleNameChange(event){
    this.setState({
      name: event.target.value,
    });
  }
  handleNameSubmit(){
    this.setState({
      step: 1,
    })
  }
  render() {
    if(this.state.step){
      return <div />;
    }else{
      return (
        <StartScreen onChange={this.handleNameChange} onClick={this.handleNameSubmit} name={this.state.name}/>  
      );
    }
  }
}

export default HobulhoApp;
